import React from 'react';
import ReactMarkdown from 'react-markdown';

import '../../App.css';
import './About.css';

import aboutme from '../../data/aboutme'
import { parseTextFile } from './textParser';

import photo from './img/photo.png'

const About = () => {

	const [content, setContent] = useState({
		name: '',
		title: '',
		paragraphs: []
  	});

	useEffect(() => {
    fetch('/aboutContent.txt')
      .then(response => response.text())
      .then(text => {
        const parsed = parseTextFile(text);
        setContent(parsed);
      });
  }, []);

  const [firstName, lastName] = content.name.split(' ');
  	// const [firstName, lastName] = aboutme.name.split('Furlan');

	return (
		<section className="section" id="about">
			<div className="about-container">
				<div className="about-content">
					<span className="font-weight-normal"> Alexandre P. </span>
					<span className="font-weight-bold">  &nbsp;Furlan</span>
					<div className="profile-info">
						<p className="profile-title">PhD Physics | Researcher | Professor | Data Engineer</p>
					</div>

				 <div className='body'>
            {content.paragraphs.map((paragraph, index) => (
              <p key={index}>
                {paragraph.split('\n').map((line, i) => (
                  <React.Fragment key={i}>
                    {line}
                    <br />
                  </React.Fragment>
                ))}
              </p>
            ))}
          </div>
        </div>
				<div className="profile-image-container">
					<img
						src={photo}
						alt="Profile"
						className="profile-img-rect"
					/>
				</div>
			</div>
		</section>
	);
};

export default About;
